00035     private static final Logger logger = Logger.getLogger(FileAclHelper.class.getName());
00036     private void logSevere(String message) {
00037         if (logger.isLoggable(Level.SEVERE)) {
00038             logger.severe(message);
00039         }
00040     }
00041     private void logFine(String message) {
00042         if (logger.isLoggable(Level.FINE )) {
00043             logger.fine(message);
00044         }
