00089     // Get the user principal for the new owner
00090     private UserPrincipal getUserPrinciple (Path targetFilePath, String newOwnerUsername) {
00091         try {
00092             return targetFilePath.getFileSystem()
00093             .getUserPrincipalLookupService()
00094             .lookupPrincipalByName(newOwnerUsername);
00095         } catch (Exception e) {
00096             errMsg.replace(0, errMsg.length(), "Error: Exception getting user principal: ").append(e.getMessage());
00097             logSevere(errMsg.toString());
00098             return null;
00099         }
00100     }
