00102     // Change the owner of the file
00103     private String setNewOwner(UserPrincipal newOwner) {
00104         try {
00105             // Change the owner of the file
00106             Files.setOwner(targetFilePath, newOwner);
00107             errMsg.replace(0, errMsg.length(), "Success: Changed owner of file").append(targetFilePath.toAbsolutePath()).append(" to ").append(newOwnerUsername);
00108             logFine(errMsg.toString());
00109             return errMsg.toString();
00110         } catch (AccessDeniedException e) {
00111             errMsg.replace(0, errMsg.length(), "Error: Access denied while changing file owner: ").append(e.getMessage());
00112             logSevere(errMsg.toString());
00113             return errMsg.toString();
00114         } catch (FileSystemException e) {
00115             errMsg.replace(0, errMsg.length(), "Error: File system error while changing file owner: ").append(e.getMessage());
00116             logSevere(errMsg.toString());
00117             return errMsg.toString();
00118         }
00119         catch (Exception e) {
00120             errMsg.replace(0, errMsg.length(), "Error: Exception while changing file owner: ").append(e.getMessage());
00121             logSevere(errMsg.toString());
00122             return errMsg.toString();
00123         }
00124     }
