00126     // Check the OS and call the appropriate method to change the permissions
00127     private String setNewPermissions(UserPrincipal newOwner) {
00128         String os = System.getProperty("os.name");
00129         if (os.startsWith("Windows")) {
00130             return setNewPermissionsWindows(newOwner);
00131         } else {
00132             return setNewPermissionsUnix();
00133         }
00134     }
