00136     // Change the permissions of the file using ACL on Windows OS
00137     private String setNewPermissionsWindows(UserPrincipal newOwner) {
00138         try {
00139             String statusMessage;
00140             // Change the permissions of the file using ACLs
00141             AclFileAttributeView aclView = Files.getFileAttributeView(targetFilePath, AclFileAttributeView.class);
00142             AclEntryPermission readPermission = AclEntryPermission.READ_DATA;
00143             AclEntryPermission writePermission = AclEntryPermission.WRITE_DATA;
00144             AclEntryPermission executePermission = AclEntryPermission.EXECUTE;
00145 
00146             AclEntryType allow = AclEntryType.ALLOW;
00147 
00148             EnumSet<AclEntryPermission> actualPermissions = EnumSet.noneOf(AclEntryPermission.class);
00149             if (newPermissions.contains("r")) {
00150                 actualPermissions.add(readPermission);
00151             }
00152             if (newPermissions.contains("w")) {
00153                 actualPermissions.add(writePermission);
00154             }
00155             if (newPermissions.contains("x")) {
00156                 actualPermissions.add(executePermission);
00157             }
00158 
00159             statusMessage = String.format("Attempting to change permissions to %s", actualPermissions);
00160             logFine(statusMessage);
00161 
00162             // Create the new ACL entry
00163             AclEntry aclEntry = AclEntry.newBuilder()
00164                     .setType(allow)
00165                     .setPrincipal(newOwner)
00166                     .setPermissions(actualPermissions)
00167                     .build();
00168 
00169             // Get the existing ACL and add the new ACL entries
00170             List<AclEntry> acl = new ArrayList<>();
00171             acl.add(aclEntry);
00172 
00173             // Set the new ACL
00174             aclView.setAcl(acl);
00175             errMsg.replace(0, errMsg.length(), "Success: Changed permissions of file").append(targetFilePath.toAbsolutePath()).append(" to ").append(actualPermissions);
00176             logFine(errMsg.toString());
00177             return errMsg.toString();
00178         } catch (Exception e) {
00179             errMsg.replace(0, errMsg.length(), "Error: Exception setting permissions on file with ACL: ")
00180                 .append(targetFilePath.toAbsolutePath())
00181                 .append(", ")
00182                 .append(e.getMessage());
00183             logSevere(errMsg.toString());
00184             return errMsg.toString();
00185         }
00186     }
