00188     // Change the permissions of the file using POSIX on Unix OS
00189     private String setNewPermissionsUnix() {
00190         String statusMessage;
00191 
00192         // Build the new permissions
00193         Set<PosixFilePermission> permissions = new HashSet<>();
00194         if (newPermissions.contains("r")) {
00195             permissions.add(PosixFilePermission.OWNER_READ);
00196         }
00197         if (newPermissions.contains("w")) {
00198             permissions.add(PosixFilePermission.OWNER_WRITE);
00199         }
00200         if (newPermissions.contains("x")) {
00201             permissions.add(PosixFilePermission.OWNER_EXECUTE);
00202         }
00203 
00204         // Change the permissions of the file using POSIX
00205         try {
00206             statusMessage = String.format("Attempting to change permissions to %s", permissions);
00207             logFine(statusMessage);
00208             Files.setPosixFilePermissions(targetFilePath.toAbsolutePath(), permissions);
00209             errMsg.replace(0, errMsg.length(), "Success: Changed permissions of file").append(targetFilePath.toAbsolutePath()).append(" to ").append(permissions);
00210             logFine(errMsg.toString());
00211             return errMsg.toString();
00212         } catch (Exception e) {
00213             errMsg.replace(0, errMsg.length(), "Error: Exception setting permissions on file with POSIX: ")
00214                 .append(targetFilePath.toAbsolutePath())
00215                 .append(", ")
00216                 .append(e.getMessage());
00217             logSevere(errMsg.toString());
