00075     /**
00076      * This method is used to get the json configurations
00077      * @param configJsonObject
00078      */
00079     public FileValidator(JSONObject configJsonObject) throws IllegalArgumentException {
00080         if (configJsonObject == null || configJsonObject.isEmpty() || !configJsonObject.has("Validations")) {
00081             throw new IllegalArgumentException("Config JSON object cannot be null or empty, and must have Validations section.");
00082         }
00083         this.configJsonObject = configJsonObject;
00084         if (configJsonObject.has("Plugins")) {
00085             try {
00086                 pluginsHelper = new PluginsHelper(configJsonObject.getJSONObject("Plugins"));
00087                 loadPlugins();
00088                 checkPluginsConfig();
00089             } catch (Exception e) {
00090                 logWarn("Error initializing plugins: " + e.getMessage());
00091                 throw new IllegalArgumentException("Error initializing plugins: " + e.getMessage());
00092             }
00093         }
00094     }
00095 
00096     private void loadPlugins() {
00097         for (PluginConfig pluginConfig : pluginsHelper.getPluginConfigs().values()) {
00098             for (String step : pluginConfig.getStepConfigs().keySet()) {
00099                 StepConfig stepConfig = pluginConfig.getStepConfigs().get(step);
00100                 stepConfigsBefore.put(stepConfig.isRunBefore()? step : "", stepConfig.isRunBefore()? stepConfig : null);
00101                 stepConfigsAfter.put(stepConfig.isRunAfter()? step : "", stepConfig.isRunAfter()? stepConfig : null);
00102             }
00103         }
00104     }
