00106     private void checkPluginsConfig() {
00107         // check that all extensions defined plugins exist in maps
00108         JSONObject validationsJsonObject = configJsonObject.getJSONObject("Validations");
00109         for (int i = 0; i < validationsJsonObject.length(); i++) {
00110             String categroyKey = validationsJsonObject.names().getString(i);
00111             for (int k=0; k < validationsJsonObject.getJSONObject(categroyKey).length(); k++) {
00112                 String extensionKey = validationsJsonObject.getJSONObject(categroyKey).names().getString(k);
00113                 if (validationsJsonObject.getJSONObject(categroyKey).getJSONObject(extensionKey).has("extension_plugins")) {
00114                     for (String pluginName : validationsJsonObject.getJSONObject(categroyKey).getJSONObject(extensionKey).getJSONArray("extension_plugins").toList().toArray(new String[0])) {
00115                         if (!stepConfigsBefore.containsKey(pluginName) && !stepConfigsAfter.containsKey(pluginName)) {
00116                             sharedMessage.replace(0, sharedMessage.length(), "Step: ").append(pluginName).append(" defined in config does not exist in plugins configuration");
00117                             logWarn(sharedMessage.toString());
00118                             throw new IllegalArgumentException(sharedMessage.toString());
00119                         }
00120                     }
00121                 }
00122             }
00123         }
00124     }
