00146     /**
00147      * This method is the main entry point for validating files, initializing the validation process and variables
00148      * @param fileCategory (String) a string containing the file type category to validate the file against
00149      * @param originalFile (byte[]) a byte array containing the file bytes of the file to be validated
00150      * @param fileName (String) a string containing the name of the file to be validated
00151      * @param outputDir (String) an optional string containing path to the output directory for validated files [optional]
00152      * @return ValidationResponse (ValidationResponse) a ValidationResponse object containing the results of the validation
00153      * @throws IllegalArgumentException (IllegalArgumentException) if any of the input parameters are null or empty
00154      */
00155     public ValidationResponse validateFile(String fileCategory, byte[] originalFile,
00156             String fileName, String... outputDir) {
00157         // Get the output directory if provided
00158         String outDir = outputDir.length > 0 ? outputDir[0] : "";
00159         
00160         // Check that the input parameters are not null or empty
00161         checkMethodInputs(fileCategory, originalFile, fileName);
00162 
00163         // Initialize variables
00164         String fileExtension = getFileExtension(fileName);
00165         Extension extensionConfig;
00166         String originalFilenameClean = fileName.replaceAll("[^a-zA-Z0-9.]", "_");
00167         fileChecksum = calculateChecksum(originalFile);
00168         
00169         // Get the configuration for the file type category and extension
00170         try {
00171             extensionConfig = new Extension(fileCategory, fileExtension, configJsonObject.getJSONObject("Validations"));
00172         } catch (Exception e) {
00173             sharedMessage.replace(0, sharedMessage.length(), "Error creating Extension configurations object: ").append(originalFilenameClean);
00174             logSevere(sharedMessage.toString());
00175             return new ValidationResponse(false, sharedMessage.toString(), originalFilenameClean, null, null);
00176         }
00177 
00178         // Log the file type category being validated
00179         sharedMessage.replace(0, sharedMessage.length(), "Validating ").append(originalFilenameClean).append(", as file type: ").append(fileCategory);
00180         logInfo(sharedMessage.toString());
00181 
00182         // Check for before plugins
00183         if (extensionConfig.getExtensionPlugins() != null) {
00184             String executionResults = executeBeforePlugins(extensionConfig, fileExtension);
00185             if (executionResults.contains(". Failed for step:")) {
00186                 sharedMessage.replace(0, sharedMessage.length(), "executeBeforePlugins failed for file: ").append(originalFilenameClean).append(", Results: ").append(executionResults);
00187                 logWarn(sharedMessage.toString());
00188                 return new ValidationResponse(false, sharedMessage.toString() , originalFilenameClean, null, null);
00189             } else if (executionResults.substring(0, 12).contains(". Error for step:")) {
00190                 sharedMessage.replace(0, sharedMessage.length(), "Error executing Plugins defined to run before validations for file: ").append(originalFilenameClean).append(", Results: ").append(executionResults);
00191                 logWarn(sharedMessage.toString());
00192             } else {
00193                 logInfo(executionResults);
00194             }
00195         }  else {
00196             sharedMessage.replace(0, sharedMessage.length(), "No before plugins to execute for file: ").append(originalFilenameClean);
00197             logFine(sharedMessage.toString());
00198         }
00199         
00200         return (doValidations(originalFilenameClean, fileExtension, extensionConfig, originalFile, fileChecksum, outDir));
00201     }
