00203     /**
00204      * If file category was found in the config, this method is used to validate the file
00205      * @param originalFilenameClean (String) a string containing the cleaned file name
00206      * @param fileExtension (String) a string containing the file extension
00207      * @param extensionConfig (Extension) an Extension object containing the configuration for the file type category and extension
00208      * @param originalFile (byte[]) a byte array containing the file bytes of the file to be validated
00209      * @param outDir (String) a string containing the path to the output directory for validated files
00210      * @return ValidationResponse (ValidationResponse) a ValidationResponse object containing the results of the validation
00211      */
00212     private ValidationResponse doValidations(String originalFilenameClean, String fileExtension, Extension extensionConfig, byte[] originalFile, String fileChecksum, String outDir) {
00213         String commonLogString = String.format(" for file extension: %s", fileExtension);
00214         String responseAggregationFail = "";
00215         int responseMsgCountFail = 0;
00216         StringBuilder sbresponseAggregationFail = new StringBuilder(responseAggregationFail);
00217         String responseAggregationSuccess = "";
00218         int responseMsgCountSuccess = 0;
00219         StringBuilder sbresponseAggregationSuccess = new StringBuilder(responseAggregationSuccess);
00220 
00221     
00222         // Check that the file size is not greater than the maximum allowed size, dont continue if it is
00223         if (Boolean.FALSE.equals(checkFileSize(originalFile.length, extensionConfig.getMaxSize()))) {
00224             sbresponseAggregationFail.append(System.lineSeparator() + ++responseMsgCountFail + ". ")
00225                 .append("File size (")
00226                 .append(originalFile.length / 1000)
00227                 .append("KB) exceeds maximum allowed size (")
00228                 .append(extensionConfig.getMaxSize()).append("KB)")
00229                 .append(commonLogString);
00230             responseAggregationFail = sbresponseAggregationFail.toString();
00231             logWarn(responseAggregationFail);
00232             return new ValidationResponse(false, responseAggregationFail, originalFilenameClean, originalFile, fileChecksum);
00233         } else {
00234             sbresponseAggregationSuccess.append(System.lineSeparator() + ++responseMsgCountSuccess + ". ")
00235                 .append("File size check passed, file size: ")
00236                 .append(originalFile.length / 1000)
00237                 .append("KB");
00238             responseAggregationSuccess = sbresponseAggregationSuccess.toString();
00239             logFine(responseAggregationSuccess);
00240         }
00241 
00242         // Check that the mime type is allowed
00243         if (!checkMimeType(originalFile, fileExtension,extensionConfig.getMimeType())) {
00244             sbresponseAggregationFail.append(System.lineSeparator() + ++responseMsgCountFail + ". ")
00245                 .append("Invalid mime_type")
00246                 .append(commonLogString);
00247             responseAggregationFail = sbresponseAggregationFail.toString();
00248             logWarn(responseAggregationFail);
00249         } else {
00250             sbresponseAggregationSuccess.append(System.lineSeparator() + ++responseMsgCountSuccess + ". ")
00251                 .append("Mime type check passed, mime type: ")
00252                 .append(extensionConfig.getMimeType());
00253             responseAggregationSuccess = sbresponseAggregationSuccess.toString();
00254             logFine(responseAggregationSuccess);
00255         }
00256 
00257         // Check that the file contains the magic bytes
00258         if (extensionConfig.getMagicBytes().isEmpty() || !containsMagicBytes(originalFile, extensionConfig.getMagicBytes())) {
00259             sbresponseAggregationFail.append(System.lineSeparator() + ++responseMsgCountFail + ". ")
00260                 .append("Invalid magic_bytes")
00261                 .append(commonLogString);
00262             responseAggregationFail = sbresponseAggregationFail.toString();
00263             logWarn(responseAggregationFail);
00264         } else {
00265             sbresponseAggregationSuccess.append(System.lineSeparator() + ++responseMsgCountSuccess + ". ")
00266                 .append("Magic bytes check passed, magic bytes: ")
00267                 .append(extensionConfig.getMagicBytes());
00268             responseAggregationSuccess = sbresponseAggregationSuccess.toString();
00269             logFine(responseAggregationSuccess);
00270         }
00271 
00272         // Check header signatures (optional)
00273         if (extensionConfig.getHeaderSignatures() != null && !containsHeaderSignatures(originalFile, extensionConfig.getHeaderSignatures())) {
00274             sbresponseAggregationFail.append(System.lineSeparator() + ++responseMsgCountFail + ". ")
00275                 .append("Invalid header_signatures")
00276                 .append(commonLogString);
00277             responseAggregationFail = sbresponseAggregationFail.toString();
00278             logWarn(responseAggregationFail);
00279         } else {
00280             sbresponseAggregationSuccess.append(System.lineSeparator() + ++responseMsgCountSuccess + ". ")
00281                 .append("Header signatures check passed, header signatures: ")
00282                 .append(extensionConfig.getHeaderSignatures());
00283             responseAggregationSuccess = sbresponseAggregationSuccess.toString();
00284             logFine(responseAggregationSuccess);
00285         }
00286 
00287         // Check footer signatures (optional)
00288         if (extensionConfig.getFooterSignatures() != null && !containsFooterSignatures(originalFile, extensionConfig.getFooterSignatures())) {
00289             sbresponseAggregationFail.append(System.lineSeparator() + ++responseMsgCountFail + ". ")
00290                 .append("Invalid footer_signatures")
00291                 .append(commonLogString);
00292             responseAggregationFail = sbresponseAggregationFail.toString();
00293             logWarn(responseAggregationFail);
00294         } else {
00295             sbresponseAggregationSuccess.append(System.lineSeparator() + ++responseMsgCountSuccess + ". ")
00296                 .append("Footer signatures check passed, footer signatures: ")
00297                 .append(extensionConfig.getFooterSignatures());
00298             responseAggregationSuccess = sbresponseAggregationSuccess.toString();
00299             logFine(responseAggregationSuccess);
00300         }
00301 
00302         
00303         // Check for after plugins
00304         if (extensionConfig.getExtensionPlugins() != null) {
00305             String executionResults = executeAfterPlugins(extensionConfig, fileExtension);
00306             if (executionResults.contains(". Failed for step:")) {
00307                 sbresponseAggregationFail.append(System.lineSeparator() + ++responseMsgCountFail + ". ")
00308                     .append("Error in executeAfterPlugins: ")
00309                     .append(executionResults)
00310                     .append(commonLogString);
00311                 responseAggregationFail = sbresponseAggregationFail.toString();
00312                 logWarn(responseAggregationFail);
00313             } else if (executionResults.contains(". Error for step:")) {
00314                 logWarn("Error in executeAfterPlugins: " + executionResults);
00315             } else {
00316                 sbresponseAggregationSuccess.append(System.lineSeparator() + ++responseMsgCountSuccess + ". ")
00317                     .append("executeAfterPlugins executed successfully: ")
00318                     .append(executionResults);
00319                     responseAggregationSuccess = sbresponseAggregationSuccess.toString();
00320                     logFine(responseAggregationSuccess);
00321             }
00322         } else {
00323             sbresponseAggregationSuccess.append(System.lineSeparator() + ++responseMsgCountSuccess + ". ")
00324                 .append("No after plugins to execute");
00325             responseAggregationSuccess = sbresponseAggregationSuccess.toString();
00326             logFine(responseAggregationSuccess);
00327         }
00328             
00329         // Check if file passed all defined validations, return false and reason if not.
00330         if (responseMsgCountFail > 0) {
00331             logFine(responseAggregationFail);
00332             return new ValidationResponse(false, responseAggregationFail, originalFilenameClean, originalFile, fileChecksum);
00333         } else {
00334             logInfo(responseAggregationSuccess);
00335         }
00336 
00337         // Check if the file name should be encoded
00338         String encodedFileName = "";
00339         if (extensionConfig.isNameEncoding()) { 
00340             sharedMessage.replace(0, sharedMessage.length(), Base64.getEncoder().encodeToString(originalFilenameClean.getBytes(StandardCharsets.UTF_8))).append(".").append(fileExtension);
00341             encodedFileName = sharedMessage.toString();
00342             String encodingStatus = String.format("File name: '%s' has been successfully encoded to: '%s'", originalFilenameClean, encodedFileName);
00343             logInfo(encodingStatus);
00344         }
00345         
00346         String targetFileName = encodedFileName.isEmpty() ? originalFilenameClean : encodedFileName;
00347 
00348         // Check if the file should be saved to output directory
00349         String savedFilePath;
00350         if (!outDir.isEmpty()) {
00351             savedFilePath = saveFileToOutputDir(outDir, targetFileName, originalFile, extensionConfig);
00352             if (savedFilePath.contains("Error:")) {
00353                 // Return valid file response if file failed to save to output directory
00354                 sharedMessage.replace(0, sharedMessage.length(), "File is valid but failed to save to output directory: ").append(savedFilePath);
00355                 return new ValidationResponse(true, sharedMessage.toString(), originalFilenameClean, originalFile, fileChecksum);
00356             }
00357             // Return valid file response if file was saved to output directory
00358             sharedMessage.replace(0, sharedMessage.length(), "File is valid and was saved to output directory: ").append(savedFilePath);
00359             return new ValidationResponse(true, sharedMessage.toString() , originalFilenameClean, originalFile, fileChecksum, savedFilePath);
00360         }
00361 
00362         // Return valid response if file passed all validations but is not meant to be saved to disk
00363         sharedMessage.replace(0, sharedMessage.length(), "File is valid: ").append(originalFilenameClean);
00364         String validMessage = sharedMessage.toString();
00365         logInfo(validMessage);
00366         return new ValidationResponse(true, validMessage, originalFilenameClean, originalFile, fileChecksum);
00367     }
