00381     private String executeBeforePlugins(Extension extensionConfig, String fileExtension) {
00382         String responseAggregation = "";
00383         char responseMsgCount = 'a';
00384         StringBuilder sbResponseAggregation = new StringBuilder(responseAggregation);
00385 
00386         for (int i = 0; i < extensionConfig.getExtensionPlugins().length(); i++) {
00387             String extensionPlugin = extensionConfig.getExtensionPlugins().getString(i);
00388             for (String step : stepConfigsBefore.keySet()) {
