00406                             .append(sharedString + stepResults);
00407                             logFine(stepResults);
00408                         ++responseMsgCount;
00409                         responseAggregation = sbResponseAggregation.toString();
00410                     } else {
00411                         sbResponseAggregation.append(System.lineSeparator()).append("\t")  .append(responseMsgCount + ". ")
