00473     /**
00474      * Execute a single plugin step
00475      * @param extensionPlugin (String) a string containing the name of the plugin step to execute
00476      * @param stepConfigs (Map<String, StepConfig>) a map containing the configuration for the step to execute
00477      * @param fileExtension (String) the file extension of the file being validated
00478      * @return String (String) a string containing the results of the plugin execution
00479      */
00480     private String executePlugin(String extensionPlugin, Map<String, StepConfig> stepConfigs , String fileExtension) {
00481         Map<String, String> stepResultsMap = new HashMap<>();
00482         String extensionPluginName = stepConfigs.get(extensionPlugin).getName();
00483         sharedMessage.replace(0, sharedMessage.length(), "Step: ").append(extensionPluginName);
00484         logFine(sharedMessage.toString());
00485         
00486         if (stepConfigs.get(extensionPlugin).getType().equals("cli")) {
00487             Map<String, Map<String, String>> stepResults = stepConfigs.get(extensionPlugin)
00488                 .getCliPluginHelper()
00489                 .execute(fileExtension, originalFile, fileChecksum);    
00490             stepResultsMap.putAll(stepResults.get(stepResults.keySet().toArray()[0]));
00491 
00492             if (!stepResultsMap.isEmpty() && stepResults.containsKey("Success")) {
00493                 String newFilePath = stepResultsMap.get(extensionPluginName.substring(extensionPluginName.lastIndexOf(".")+1,
00494                 extensionPluginName.length()) + ".filePath");
00495                 String newB64Content = stepResultsMap.get(extensionPluginName.substring(extensionPluginName.lastIndexOf(".")+1,
00496                 extensionPluginName.length()) + ".fileContent");
00497 
00498                 if (!isBlank(newFilePath)) {
00499                     try {
00500                         Path newFile = new File(stepResultsMap.get(extensionPluginName.substring(extensionPluginName.lastIndexOf(".")+1, 
00501                         extensionPluginName.length()) + ".filePath")).toPath();
00502                         originalFile = Files.readAllBytes(newFile);
00503                         fileChecksum = calculateChecksum(originalFile);
00504                         deleteTempDir(newFile.getParent().toAbsolutePath());
00505                     } catch (IOException e) {
00506                         sharedMessage.replace(0, sharedMessage.length(), "Error reading plugin expected file: ").append(e.getMessage());
00507                         logWarn(String.format(sharedMessage.toString()));
00508                         return sharedMessage.toString();
00509                     }
00510                 }
00511                 if (!isBlank(newB64Content)) {
00512                     try {
00513                         originalFile = Base64.getDecoder().decode(newB64Content);
00514                     } catch (Exception e) {
00515                         sharedMessage.replace(0, sharedMessage.length(), "Error decoding plugin expected file: ").append(e.getMessage());
00516                         logWarn(String.format(sharedMessage.toString()));
00517                         return sharedMessage.toString();
00518                     }
00519                     fileChecksum = calculateChecksum(originalFile);
00520                 }
00521             }
00522             for(Map.Entry<String, String> entry : stepResultsMap.entrySet()) {
00523                 String errorMsg =  entry.getValue();
00524                 String errorDetails = entry.getKey();
00525                 sharedMessage.replace(0, sharedMessage.length(), "Step: ").append(extensionPluginName)
00526                     .append(" Success, Results: ")
00527                     .append(errorDetails)
00528                     .append("\"")
00529                     .append(errorMsg);
00530                 logFine(sharedMessage.toString());
00531             }
00532             return sharedMessage.toString();
00533         } else if (stepConfigs.get(extensionPlugin).getType().equals("http")) {
00534             // TODO: Implement http plugin type
00535         }
00536         return sharedMessage.toString();
00537     }
