00551     // Save the file to temporary directory for analysis
00552     private Path saveFileToTempDir(String fileExtension, byte[] originalFile) {
00553         Path tempFilePath = null;
00554         try {
00555             // Create a temporary directory
00556             Path tempDir = Files.createTempDirectory("tempDir");
00557             tempFilePath = Files.createTempFile(tempDir, "tempFile", "." + fileExtension);
00558             Files.write(tempFilePath, originalFile);
00559         } catch (Exception e) {
00560             sharedMessage.replace(0, sharedMessage.length(), "Error: Saving file to temporary directory failed: ").append(e.getMessage());
00561             logWarn(sharedMessage.toString());
00562             return null;
00563         }
00564         return tempFilePath;
00565     }
