00567     // Explicitley delete the temporary directory and file
00568     private Boolean deleteTempDir(Path tempFilePath) {
00569         try (Stream<Path> walk = Files.walk(tempFilePath)) {
00570             walk.sorted(Comparator.reverseOrder())
00571             .map(Path::toFile)
00572             .forEach(File::delete);
00573             return true;
00574         } catch (Exception e) {
00575             sharedMessage.replace(0, sharedMessage.length(), "Error: Delere temporary directoy failed: ").append(e.getMessage());
00576             logWarn(sharedMessage.toString());
00577             return false;
00578         }
00579     }
