00581     // Get the file mime type from the file bytes and checks if it matches allowed mime types
00582     private boolean checkMimeType(byte[] originalFileBytes, String fileExtension, String mimeType) {
00583         String fileMimeType = "";
00584         Path tempFile = saveFileToTempDir(fileExtension, originalFileBytes);
00585         if (tempFile == null) {
00586             return false;
00587         }
00588         try {
00589             fileMimeType = Files.probeContentType(tempFile);
00590             deleteTempDir(tempFile);
00591         } catch (Exception e) {
00592             logWarn("checkMimeType failed: " + e.getMessage());
00593         }
00594         return fileMimeType != null && !isBlank(fileMimeType) && fileMimeType.equals(mimeType);
00595     }
