00597     // Get the file extension from the file name
00598     private String getFileExtension(String fileName) {
00599         int dotIndex = fileName.lastIndexOf('.');
00600         if (dotIndex == -1 || dotIndex == fileName.length() - 1) {
00601             return "";
00602         }
00603         return fileName.substring(dotIndex + 1);
00604     }
