00606     // Check if the file contains the magic bytes
00607     private boolean containsMagicBytes(byte[] originalFileBytes, String magicBytesPattern) {
00608         if (originalFileBytes.length == 0 || magicBytesPattern == null || magicBytesPattern.isEmpty()) {
00609             return false;
00610         }
00611         magicBytesPattern = magicBytesPattern.replaceAll("\\s", "");
00612         if (magicBytesPattern.length() % 2 != 0) {
00613             magicBytesPattern = "0" + magicBytesPattern;
00614         }
00615         byte[] magicBytes = new byte[magicBytesPattern.length() / 2];
00616         for (int i = 0; i < magicBytesPattern.length(); i += 2) {
00617             magicBytes[i / 2] = (byte) Integer.parseInt(magicBytesPattern.substring(i, i + 2), 16);
00618         }
00619         for (int i = 0; i < originalFileBytes.length - magicBytes.length; i++) {
00620             boolean found = true;
00621             for (int j = 0; j < magicBytes.length; j++) {
00622                 if (originalFileBytes[i + j] != magicBytes[j]) {
00623                     found = false;
00624                     break;
00625                 }
00626             }
00627             if (found) {
00628                 return true;
00629             }
00630         }
00631         return false;
00632     }
00633 
00634     // Check if the file contains the header signatures
00635     private boolean containsHeaderSignatures(byte[] fileBytes, String headerSignaturesPattern) {
00636         if (isBlank(headerSignaturesPattern)) {
00637             return true;
00638         }
00639         String hexPattern = headerSignaturesPattern.replaceAll("\\s", "");
00640         if (hexPattern.length() % 2 != 0) {
00641             hexPattern = "0" + hexPattern;
00642         }
00643         byte[] headerSignatures = new byte[hexPattern.length() / 2];
00644         for (int i = 0; i < hexPattern.length(); i += 2) {
00645             headerSignatures[i / 2] = (byte) Integer.parseInt(hexPattern.substring(i, i + 2), 16);
00646         }
00647         for (int i = 0; i < headerSignatures.length; i++) {
00648             if (i >= fileBytes.length || fileBytes[i] != headerSignatures[i]) {
00649                 return false;
00650             }
00651         }
00652         return true;
00653     }
00654 
00655     // Check if the file contains the footer signatures
00656     private boolean containsFooterSignatures(byte[] fileBytes, String footerSignaturesPattern) {
00657         if (isBlank(footerSignaturesPattern)) {
00658             return true;
00659         }
00660         String hexPattern = footerSignaturesPattern.replaceAll("\\s", "");
00661         if (hexPattern.length() % 2 != 0) {
00662             hexPattern = "0" + hexPattern;
00663         }
00664         byte[] footerSignatures = new byte[hexPattern.length() / 2];
00665         for (int i = 0; i < hexPattern.length(); i += 2) {
00666             footerSignatures[i / 2] = (byte) Integer.parseInt(hexPattern.substring(i, i + 2), 16);
00667         }
00668         int footerStartIndex = fileBytes.length - footerSignatures.length -1;
00669         if (footerStartIndex < 0) {
00670             return false;
00671         }
00672         for (int i = 0; i < footerSignatures.length; i++) {
00673             if (fileBytes[footerStartIndex + i] != footerSignatures[i]) {
00674                 return false;
00675             }
00676         }
00677         return true;
00678     }
