00680     // Calculate file checksum
00681     private static String calculateChecksum(byte[] fileBytes) {
00682         try {
00683             byte[] hash = MessageDigest.getInstance("SHA-256").digest(fileBytes);
00684             return new BigInteger(1, hash).toString(16);
00685         } catch (Exception e) {
00686             e.printStackTrace();
00687             return null;
00688         }
00689     }   
