00691     // save the file to the output directory with appropriate owner and permissions
00692     private String saveFileToOutputDir(String outDir, String fileName, byte[] fileBytes, Extension extensionConfig) {
00693         Path targetFilePath = Paths.get(outDir, fileName);
00694         try {
00695             Files.write(targetFilePath, fileBytes, StandardOpenOption.CREATE);
00696         } catch (IOException e) {
00697             sharedMessage.replace(0, sharedMessage.length(), "Error: Saving file to directory failed: ").append(e.getMessage());
00698             logSevere(sharedMessage.toString());
00699             return sharedMessage.toString();
00700         }
00701         // Try to set the file owner and permissions
00702         FileAclHelper fileAclHelper = new FileAclHelper();
00703         String newFileAttributesStatus = fileAclHelper.changeFileAcl(targetFilePath, extensionConfig.getChangeOwnershipUser(), extensionConfig.getChangeOwnershipMode());
00704         if (newFileAttributesStatus.contains("Error:")) {
00705             try {
00706                 Files.deleteIfExists(targetFilePath);
00707             } catch (IOException e) { 
00708                 logSevere("Error: Failed to delete file from permissions change operation: " + e.getMessage());
00709             }
00710             logSevere(newFileAttributesStatus);
00711             return newFileAttributesStatus;
00712         }
00713         // Return the full path to the saved file since no errors were encountered
00714         return targetFilePath.toAbsolutePath().toString();
00715     }
