00024     /**
00025      * Returns a list of PluginConfig objects.
00026      * @return (List<PluginConfig>) a list of PluginConfig objects
00027      */
00028     public Map<String, PluginConfig> getPluginConfigs() {
00029         HashMap<String, PluginConfig> pluginConfigs = new HashMap<>();
00030         Map<String, StepConfig> stepConfigs = new HashMap<>();
00031 
00032 
00033         for (String pluginName : plugins.keySet()) {
00034             JSONObject plugin = plugins.getJSONObject(pluginName);
00035             PluginConfig pluginConfig = new PluginConfig();
00036             pluginConfig.setName(pluginName);
00037 
00038             for (String stepName : plugin.keySet()) {
00039                 JSONObject step = plugin.getJSONObject(stepName);
00040                 StepConfig stepConfig = new StepConfig();
00041                 String pluginStepName = pluginName + "." + stepName.substring(0, stepName.lastIndexOf('.'));
00042                 stepConfig.setName(pluginStepName);
00043 
00044                 stepConfig.setType(step.getString("type"));
00045                 stepConfig.setRunBefore(step.optBoolean("run_before"));
00046                 stepConfig.setRunAfter(step.optBoolean("run_after"));
00047                 stepConfig.setCredsPath(step.optString("creds_path"));
00048                 stepConfig.setTimeout(step.getInt("timeout"));
00049                 stepConfig.setOnFail(step.getString("on_timeout_or_fail"));
00050                 stepConfig.setEndpoint(step.getString("endpoint"));
00051                 stepConfig.setResponse(step.getString("response"));
00052 
00053                 switch (stepConfig.getType()) {
00054                     case "cli":
00055                         CliPluginHelper cliPluginStep = new CliPluginHelper(stepConfig);
00056                         stepConfig.setCliPluginHelper(cliPluginStep);
00057                         stepConfigs.put(pluginStepName, stepConfig);
00058 
00059                         continue;
00060                     case "http":
00061                         stepConfig.setMethod(step.optString("method"));
00062                         stepConfig.setHeaders(step.optJSONObject("headers"));
00063                         stepConfig.setBody(step.optJSONObject("body"));
00064                         stepConfig.setHttpPassCode(step.optInt("http_pass_code"));
00065                         stepConfig.setHttpFailCode(step.optInt("http_fail_code"));
00066                         stepConfigs.put(pluginStepName, stepConfig);
00067                         continue;
00068                     default:
00069                         continue;
00070                 }
00071             }
00072             pluginConfig.setStepConfigs(stepConfigs);
00073             pluginConfigs.put(pluginName, pluginConfig);
00074         }
00075         return pluginConfigs;
00076     }
